css.style:
form {
  background-color: white;
  font-family: 'Lato', 'sans-serif';
  color: #575756;
  padding: 20px;
  border: 1px solid rgba(0, 0, 0, .1);
  border-radius: 5px;
  box-shadow: 0 5px 8px 0 rgb(0 0 0 / 10%), 0 3px 10px 0 rgb(0 0 0 / 10%);
} ._category h1, h2, h3, h4, h5, h6 {
  font-size: 14px;
  font-weight: 500;
  line-height: 1.15em;
  margin: 0.3em 0;
} ._category h1 { font-size: 22px; } ._category h2 { font-size: 18px; } ._category {
  border: 0;
  border-bottom: 2px dashed #0000004d;
  margin: 10px 0 20px 0;
}
._control {
  margin: 5px 0;
} ._label {
  display: block;
} ._input {
  padding: 0 0.5em;
}
._input[multiple] {
  height: 6.5em;
  display: grid;
  align-items: center;
}
._input, .language {
  margin: 2px 0;
  width: 100%;
  height: 2.5em;
  background-color: white;
  border-radius: 3px;
  border-width: 1px;
} ._information {
  color: #6A6A6A;
  font-size: 14px;
  font-style: italic;
  line-height: 1.5;
  text-align: justify;
} .language {
  width: 100px;
  height: 1.75em;
  float: right;
} #submit {
  margin-top: 10px;
  width: 150px;
  height: 2em;
  background-color: white;
  border-width: 1px;
  border-radius: 3px;
} #submit:hover {
  background-color: lightgray;
}

